    public int getChanelId(String voiceData) {
        int chId = 0;
        if (voiceData == null) {
            return chId;
        }

        if (Arrays.asList(DATA_CH1).contains(voiceData)) {
            chId = KEY_CAHNEL_1;
        } else if (Arrays.asList(DATA_CH2).contains(voiceData)) {
            chId = KEY_CAHNEL_2;
        } else if (Arrays.asList(DATA_CH3).contains(voiceData)) {
            chId = KEY_CAHNEL_3;
        } else if (Arrays.asList(DATA_CH4).contains(voiceData)) {
            chId = KEY_CAHNEL_4;
        } else if (Arrays.asList(DATA_CH5).contains(voiceData)) {
            chId = KEY_CAHNEL_5;
        } else if (Arrays.asList(DATA_CH6).contains(voiceData)) {
            chId = KEY_CAHNEL_6;
        } else if (Arrays.asList(DATA_CH7).contains(voiceData)) {
            chId = KEY_CAHNEL_7;
        } else if (Arrays.asList(DATA_CH8).contains(voiceData)) {
            chId = KEY_CAHNEL_8;
        } else if (Arrays.asList(DATA_CH9).contains(voiceData)) {
            chId = KEY_CAHNEL_9;
        } else if (Arrays.asList(DATA_CH10).contains(voiceData)) {
            chId = KEY_CAHNEL_10;
        } else if (Arrays.asList(DATA_CH11).contains(voiceData)) {
            chId = KEY_CAHNEL_11;
        } else if (Arrays.asList(DATA_CH12).contains(voiceData)) {
            chId = KEY_CAHNEL_12;
            // ここからは確認用
        } else if (Arrays.asList(dataEPG).contains(voiceData)) {
            chId = KEY_EPG;
        } else if (Arrays.asList(DATA_UP).contains(voiceData)) {
            chId = KEY_UP;
        } else if (Arrays.asList(DATA_LEFT).contains(voiceData)) {
            chId = KEY_LEFT;
        } else if (Arrays.asList(DATA_RIGHT).contains(voiceData)) {
            chId = KEY_RIGHT;
        } else if (Arrays.asList(DATA_DOWN).contains(voiceData)) {
            chId = KEY_DOWN;
        } else if (Arrays.asList(DATA_ENTER).contains(voiceData)) {
            chId = KEY_ENTER;
        } else if (Arrays.asList(dataBack).contains(voiceData)) {
            chId = KEY_BACK;
        }
        // ここまで確認用
        return chId;
    }

